changeSet.sql:
DROP TRIGGER IF EXISTS set_created_at_in_ballots_trigger ON ballots;
DROP FUNCTION IF EXISTS set_created_at;
CREATE FUNCTION set_created_at()
    RETURNS TRIGGER AS $$
BEGIN
    NEW.created_at = NOW();
RETURN NEW;
END;
$$ LANGUAGE plpgsql;
CREATE TRIGGER set_created_at_in_ballots_trigger
    BEFORE INSERT ON ballots
    FOR EACH ROW
    EXECUTE FUNCTION set_created_at();
